// Plugin Config
function oscp_plugin_add_shortcode($atts) {
	$oscp_options = get_option('oscp_config_data');
	$oscpConfig = array(
		'src' => plugins_url() . '/bullhorn-oscp/#/',
		'width' => esc_attr($oscp_options['oscp_width']),
		'height' => esc_attr($oscp_options['oscp_height']),
		'scrolling' => esc_attr($oscp_options['oscp_scrollable']),
		'class' => 'iframe-class',
		'frameborder' => '0'
	);

	oscp_save_file(true);

	$html = "\n".'<!-- oscp plugin v' . OSCP_PLUGIN_VERSION . ' -->' . "\n";
	$html .= '<iframe';
	foreach($oscpConfig as $attr => $value) {
		if (strtolower($attr) != 'same_height_as'
            AND strtolower($attr) != 'onload'
            AND strtolower($attr) != 'onpageshow'
            AND strtolower($attr) != 'onclick') {
			if ($value != '') {
				$html .= ' ' . esc_attr($attr) . '="' . esc_attr($value) . '"';
			} else {
				$html .= ' ' . esc_attr($attr);
			}
		}
	}
	$html .= '></iframe>'."\n";

	if (isset($atts["same_height_as"])) {
		$html .= '
			<script>
			document.addEventListener("DOMContentLoaded", function(){
				var target_element, iframe_element;
				iframe_element = document.querySelector("iframe.' . esc_attr( $atts["class"] ) . '");
				target_element = document.querySelector("' . esc_attr( $atts["same_height_as"] ) . '");
				iframe_element.style.height = target_element.offsetHeight + "px";
			});
			</script>
		';
	}

	return $html;
}


function oscp_save_file($onload) {
	$oscp_options = get_option('oscp_config_data');

	// Create JSON File:
	$fileExists = file_exists(OSCP_APP_JSON_PATH);
	if($fileExists){
		if($onload) {
			return;
		}
		$jsonString = file_get_contents(OSCP_APP_JSON_PATH);
		$data = json_decode($jsonString, true);
	}
    $data['acceptedResumeTypes'] = json_decode($oscp_options['oscp_resume_types']);
    $data['companyName'] = $oscp_options['oscp_company_name'];
    $data['defaultLocale'] = $oscp_options['oscp_locale'];
	$data['supportedLocales'] = explode(',','en-US,en-GB,en,fr-FR,fr,es,de,it,nl,ru,zh-CN');
    $data['minUploadSize'] = 4096;
	$data['languageDropdownOptions'] = array (
	  'enabled' => false,
	  'choices' => array(),
	); /** edit this to change language dropdown options **/
    $data['maxRelatedJobs'] = 5;
    $data['maxUploadSize'] = 5242880;
    $data['service']['batchSize'] = 500;
    $data['service']['corpToken'] = $oscp_options['oscp_corp_token'];
    $data['service']['port'] = null;
    $data['service']['swimlane'] = $oscp_options['oscp_swim_lane'];
    $data['service']['fields'] = explode(',', $oscp_options['oscp_fields']);
    $data['service']['jobInfoChips'] = explode(',', $oscp_options['oscp_job_info_chips']);
	$dateLastPublished = array (
	  'type' => 'mediumDate',
	  'field' => 'dateLastPublished',
	);
	array_push($data['service']['jobInfoChips'], $dateLastPublished);
	$data['service']['showCategory'] = true;
	$data['service']['keywordSearchFields'] = array(
      "publicDescription",
      "title"
    );

	$data['additionalJobCriteria']['field'] = $oscp_options['oscp_filter_field'];
    $data['additionalJobCriteria']['values'] = explode(',', $oscp_options['oscp_filter_values']);
    $data['additionalJobCriteria']['sort'] = is_null($oscp_options['oscp_sort']) ? '-dateLastPublished' : $oscp_options['oscp_sort'];
	$data['integrations']['googleAnalytics']['trackingId'] = $oscp_options['oscp_analytics_tracking_id'];
	$data['integrations']['googleSiteVerification']['verificationCode'] = '';
    $data['eeoc']['genderRaceEthnicity'] = ($oscp_options['oscp_gender_race_ethnicity']  === 'true');
    $data['eeoc']['veteran'] = ($oscp_options['oscp_veteran']  === 'true');
    $data['eeoc']['disability'] = ($oscp_options['oscp_disability']  === 'true');
    $data['privacyConsent']['consentCheckbox'] = ($oscp_options['oscp_consent']  === 'true');
    $data['privacyConsent']['sidebarLink'] = ($oscp_options['oscp_sidebar_link']  === 'true');
    $data['privacyConsent']['privacyPolicyUrl'] = $oscp_options['oscp_privacy_url'];
    $newJsonString = json_encode($data);
	//workaround for dealing with shared hosting file update rules
	if($fileExists) {
		unlink(OSCP_APP_JSON_PATH);
	}
    file_put_contents(OSCP_APP_JSON_PATH, $newJsonString);
}

